CREATE TABLE IF NOT EXISTS `PREFIX_gap_refund` (`id` INT UNSIGNED NOT NULL auto_increment, `shop_id` TINYINT UNSIGNED NOT NULL DEFAULT '1', `order_id` INT UNSIGNED NOT NULL, `cust_id` INT UNSIGNED NOT NULL, `sent` enum('0','1'), PRIMARY KEY (`id`), INDEX `order_id` (`order_id`), INDEX `sent` (`sent`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `PREFIX_gap_refund_partial` (`id` INT UNSIGNED NOT NULL auto_increment, `shop_id` TINYINT UNSIGNED NOT NULL DEFAULT '1', `order_id` INT UNSIGNED NOT NULL, `cust_id` INT UNSIGNED NOT NULL, `sent` enum('0','1'), PRIMARY KEY (`id`), INDEX `order_id` (`order_id`), INDEX `sent` (`sent`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;