$_MODULE['<{securitypro}prestashop>securitypro_61392abf91987608bb568c9f4e77d0e8'] = 'Cookie Secure spécifie si les cookies doivent être envoyés uniquement via des connexions sécurisées. Ce paramètre nécessite l\'activation de SSL / TLS.';
