$_MODULE['<{securitypro}prestashop>securitypro_9738426fe28a50aa318c0a437f2168ce'] = 'DROWN Vulnerability	';
$_MODULE['<{securitypro}prestashop>securitypro_a1a3f2b6fa03e3be939a58ea3a160de9'] = 'DROWN היא פגיעות חמורה המשפיעה על HTTPS ועל שירותים אחרים המסתמכים על SSL ו- TLS, חלק מהפרוטוקולים החיוניים לקריפטוגרפיה לאבטחת אינטרנט. פרוטוקולים אלה מאפשרים לכל אחד באינטרנט לגלוש באינטרנט, להשתמש בדואר אלקטרוני, לקנות באינטרנט ולשלוח הודעות מיידיות מבלי שצדדים שלישיים יוכלו לקרוא את התקשורת.';
$_MODULE['<{securitypro}prestashop>securitypro_5a6f16d26dc54481d8e481026a5451af'] = 'POODLE Vulnerability	';
