$_MODULE['<{securitypro}prestashop>securitypro_5cb902b9480bb17a70c905c0a3c0a154'] = 'Header HSTS';
$_MODULE['<{securitypro}prestashop>securitypro_774050bb1f5b2787e66b5e43936256ef'] = 'HSTS (HTTP Strict Transport Security) protegge da attacchi di downgrade del protocollo e dirottamento dei cookie. HSTS è un modo per i siti di scegliere di utilizzare sempre HTTPS. Puoi abilitare questa funzione in';
