$_MODULE['<{securitypro}prestashop>securitypro_0cea593c307abe36d21fc3d66adc2646'] = 'Váš klient TLS sa nepokúša komprimovať nastavenia, ktoré šifrujú vaše pripojenie, aby sa zabránilo úniku informácií z';
